(function (window, document) {
  const matchMedia =
    window.matchMedia ||
    function (query) {
      const mq = document.createElement("style");
      document.documentElement.firstElementChild.appendChild(mq);
      mq.innerHTML = `@media ${query} { #mq-test-1 { width: 42px; } }`;
      const matches = mq.sheet.cssRules[0].media.mediaText === query;
      document.documentElement.firstElementChild.removeChild(mq);
      return { matches };
    };

  window.matchMedia = matchMedia;
})(window, document);


